class Settings():    """Класс для хранения всех настроек игры."""    def __init__(self):        """Инициализирует статические настройки игры"""        # Параметры экрана        self.screen_width = 1200        self.screen_height = 800        self.bg_color = (20, 20, 20)        # Настройки корабля        self.ship_speed = 3        self.ship_limit = 3        # Параметры снаряда        self.bullet_speed = 10        self.bullet_width = 10        self.bullet_height = 15        self.bullet_color = (200, 0, 50)        self.bullets_allowed = 20        # Настройки пришельцев        self.alien_speed = 0.5        self.fleet_drop_speed = 15        # Темп ускорения игры        self.speedup_scale = 5        # Темп роста стоимости пришельцев        self.score_scale = 1.5        self.initialize_dynamic_settings()    def initialize_dynamic_settings(self):        """Инициализирует настройки, изменяющиеся во время игры"""        self.ship_speed_factor = 1.5        self.bullet_speed_factor = 3.0        self.alien_speed_factor = 2        # fleet_direction = 1 обозначает движение вправо; а -1 - влево.        self.fleet_direction = 1        # Подсчет очков        self.alien_points = 1    def increase_speed(self):        """Увеличивает настройки скорости и стоимость пришельцев"""        self.ship_speed_factor *= self.speedup_scale        self.bullet_speed_factor *= self.speedup_scale        self.alien_speed_factor *= self.speedup_scale        self.alien_points = int(self.alien_points * self.score_scale)